import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import { Avatar } from 'antd'

const navLinks = [
  {
    title: 'Home',
    path: '/'
  },
  {
    title: 'Blog',
    path: '/blog'
  },
  {
    title: 'Contact Us',
    path: '/contact-us'
  },
  {
    title: 'Login',
    path: '/login'
  },
]

export default class Navigation extends React.Component {

  constructor(props){
    super(props);
  }

  setMenuActive(val) {
    this.setState({menuActive:val})
  }

  render(){
    return (
      <nav className="site-navigation">
        <span className="menu-title">LadyBug CR</span>
        <div onMouseLeave={this.setMenuActive(!this.state.menuActive)} className={`menu-content-container ${this.state.menuActive && 'active'}`}>
        <ul>

          { navLinks.map((link, index) => (
            <li key={index}>
                <Link to={link.path}>
                  {link.title}
                </Link>
            </li>
          ))}
        </ul>
        <span className="menu-avatar-container">
          <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" size={38} />
          <span className="menu-avatar-name">{`${this.state.user.firstName} ${this.state.user.lastName}`}</span>
        </span>
        </div>
        <i className="ionicons icon ion-ios-menu" onClick={this.setMenuActive(!this.state.menuActive)} />

      </nav>
  )}

}
